{"changed":true,"filter":false,"title":"login_CheapoMail.php","tooltip":"/login_CheapoMail.php","value":"<?php\n$servername = \"0.0.0.0\";\n$username = \"gigaslayer\";\n// Create connection\n$conn = mysqli_connect($servername, $username);\n// Check connection\nif (!$conn) {\ndie(\"Connection failed: \" . mysql_connect_error());\n}\necho \"Connected successfully!\" . \"<br />\";\nmysql_select_db(\"CHEAPOMAIL\");\n$username = $_POST['Username'];\n$password = $_POST['Password'];\n$username = stripslashes($username);\n$password = stripslashes($password);\n$username = mysql_real_escape_string($username);\n$password = mysql_real_escape_string($password);\n$sql = \"SELECT * FROM $User WHERE Username='$username' and Password='$password'\";\n$result = mysql_query($sql);\n// Mysql_num_row is counting table row\n$count = mysql_num_rows($result);\n// If result matched $username and $password, table row must be 1 row\nif($count==1)\n{\n// Register $username, $password and redirect to file \"logon_success.php\"\n$_SESSION[\"usrnm\"] = $username;\n$_SESSION[\"pswrd\"] = $password;\nheader(\"location:logon_success.php\");\n}\nelse\n{\necho \"<br />\" . \"incorrect Username or Password\";\n}\n","undoManager":{"mark":-1,"position":14,"stack":[[{"group":"doc","deltas":[{"start":{"row":31,"column":17},"end":{"row":31,"column":22},"action":"remove","lines":["Wrong"]},{"start":{"row":31,"column":17},"end":{"row":31,"column":18},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":31,"column":18},"end":{"row":31,"column":19},"action":"insert","lines":["n"]}]}],[{"group":"doc","deltas":[{"start":{"row":31,"column":19},"end":{"row":31,"column":20},"action":"insert","lines":["c"]}]}],[{"group":"doc","deltas":[{"start":{"row":31,"column":20},"end":{"row":31,"column":21},"action":"insert","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":31,"column":21},"end":{"row":31,"column":22},"action":"insert","lines":["r"]}]}],[{"group":"doc","deltas":[{"start":{"row":